import React, {
  Component,
} from 'react'
import {
  View,
  Text,
  StyleSheet,
  TabBarIOS,
  NavigatorIOS,
  StatusBar,
} from 'react-native'

import Main from './main'
import Advertisement from './advertisement'

export default class TabBarIndex extends Component {

  // 构造
  constructor(props) {
    super(props);
    // 初始状态
    this.state = {
      opacity: 0,
      selectedTab: 'index',
    };
  }

  componentDidMount () {
    setTimeout( () => {
      this.setState({
        opacity: 1
      })
    }, 100)
  }

  render() {

    return (
      <View style={styles.container}>
        <StatusBar animated={true} barStyle={'light-content'} hidden={false} />
        <TabBarIOS style={{opacity: this.state.opacity,}} tintColor={'red'} barTintColor={'rgba(255, 255, 255, .8)'}>
          <TabBarIOS.Item
            systemIcon="more"
            selected={this.state.selectedTab === 'index'}
            onPress={() => {
            this.setState({
              selectedTab: 'index'
            })
          }}>
            <NavigatorIOS style={styles.container}
                          initialRoute={{
                    component: Main,
                    title: '100 Days of RN',
                    //navigationBarHidden: true,

                  }}
            />
          </TabBarIOS.Item>
        </TabBarIOS>
        <Advertisement countDownSeconds={2}></Advertisement>
      </View>
    )
  }

}

const base64Icon = 'data:image/png;base64,';


const styles = StyleSheet.create({
  container: {
    flex: 1,
  }
})